mapper.select:
select
	id,
	firstname,
	lastname,
	lastname_kana,
	firstname_kana,
	zip1,
	zip2,
	address_prefecture,
	address_city,
	address_ooaza,
	address_aza,
	address_other,
	tel1,
	tel2,
	tel3,
	mobile_tel1,
	mobile_tel2,
	mobile_tel3,
	email,
	password,
	setai_family,
	setai_count,
	setai_status,
	setai_year,
	setai_loan,
	income,
	savings,
	employment_status,
	company_name,
	company_zip1,
	company_zip2,
	company_address_prefecture,
	company_address_city,
	company_address_ooaza,
	company_address_aza,
	company_address_other,
	company_tel1,
	company_tel2,
	company_tel3,
	company_department,
	company_industry_type,
	company_work_years
from
	m_customer
where
	delete_flg = '0'

		and firstname = #{firstname}


		and lastname = #{lastname}


		and lastname_kana = #{lastnameKana}


		and firstname_kana = #{firstnameKana}


		and zip1 = #{zip1}


		and zip2 = #{zip2}


		and address_prefecture = #{addressPrefecture}


		and address_city = #{addressCity}


		and address_ooaza = #{addressOoaza}


		and address_aza = #{addressAza}


		and address_other = #{addressOther}


		and tel1 = #{tel1}


		and tel2 = #{tel2}


		and tel3 = #{tel3}


		and mobile_tel1 = #{mobileTel1}


		and mobile_tel2 = #{mobileTel2}


		and mobile_tel3 = #{mobileTel3}


		and email = #{email}


		and password = #{password}


		and setai_family = #{setaiFamily}


		and setai_count = #{setaiCount}


		and setai_status = #{setaiStatus}


		and setai_year = #{setaiYear}


		and setai_loan = #{setaiLoan}


		and income = #{income}


		and savings = #{savings}


		and employment_status = #{employmentStatus}
