mapper.select:

select
	sum(point) as point
from
	t_point
where
	user_id = #{userId}
	and start_date <= #{systemDate}
	and expired_date > #{systemDate}
	and used_date is null
	and delete_flg = '0'


select
	sum(point) as futurePoint
from
	t_point
where
	user_id = #{userId}
	and start_date > #{systemDate}
	and expired_date > #{systemDate}
	and used_date is null
	and delete_flg = '0'
